const mongoose = require("mongoose");
const Schema = mongoose.Schema;

//Article Schema
const articleSchema = new Schema({
  topic: {
    type: String,
    required: [true, "Topic is required"],
  },
  title: {
    type: String,
    required: [true, "Title is required"],
  },
  content: {
    type: String,
    required: [true, "Content is required"],
  },
  author_name: {
    type: String,
    required: [true, "Author name is required"],
  },
  date_of_publish: {
    type: Date,
    default: Date.now,
  },
  tags: {
    type: Array,
    of: String,
  },
  likes: {
    type: Number,
    default: 0,
  },
  dislikes: {
    type: Number,
    default: 0,
  },
  author_id: {
    type: String,
    required: [true, "Author id is required"],
  },
  image_link: {
    type: String,
    default:
      "data:image/png;base64,",
  },
  liked_userids: {
    type: Array,
    of: String,
  },
  disliked_userids: {
    type: Array,
    of: String,
  },
});

const Article = mongoose.model("article_model", articleSchema);
module.exports = Article;
